from http.client import HTTPConnection, HTTPSConnection, HTTPResponse
import ssl
import os
import time

class Stream:
    def __init__(self, server, port=80, headers={}, url="/", ssl_enabled=False, ssl_verification_enabled=True):
        self.maxtries = 5
        self._tries = 0
        self._delay = 1
        self._closed = False
        self._server = server
        self._port = port
        self._headers = headers
        self._url = url
        self._ssl_enabled = ssl_enabled
        self._ssl_verification_enabled = ssl_verification_enabled
        self._conn = None
        self._bytes = b""
        self._connect()

    def write(self, data, reconnect_on=("", 200, 502)):
        if not self._isconnected():
            self._reconnect()
        try:
            self._conn.putrequest("POST", self._url)
            for header, value in self._headers.items():
                self._conn.putheader(header, value)
            self._conn.putheader("Transfer-Encoding", "chunked")
            self._conn.endheaders()
            self._conn.send(hex(len(data))[2:].encode() + b"\r\n" + data + b"\r\n")
        except Exception as e:
            if self._tries < self.maxtries:
                self._reconnect()
                self.write(data, reconnect_on)
            else:
                raise e

    def close(self):
        if self._isconnected():
            self._conn.send(b"0\r\n\r\n")
            response = self._getresponse()
            self._conn.close()
            self._closed = True
            return response
        return b""

    def _connect(self):
        if self._ssl_enabled:
            context = self._get_ssl_context()
            self._conn = HTTPSConnection(self._server, self._port, context=context)
        else:
            self._conn = HTTPConnection(self._server, self._port)
        self._conn.connect()

    def _getresponse(self):
        try:
            response = self._conn.getresponse()
            return response.read()
        except Exception as e:
            if self._tries < self.maxtries:
                self._reconnect()
                return self._getresponse()
            else:
                raise e

    def _isconnected(self):
        return self._conn and not self._closed

    def _reconnect(self):
        self._tries += 1
        time.sleep(self._delay)
        self._delay *= 2
        self._connect()

    def _reset_retries(self):
        self._tries = 0
        self._delay = 1

    def _get_ssl_context(self):
        if not self._ssl_enabled:
            return None
        context = ssl.create_default_context()
        if not self._ssl_verification_enabled:
            context.check_hostname = False
            context.verify_mode = ssl.CERT_NONE
        return context

    def _get_proxy_config(self):
        http_proxy = os.environ.get('http_proxy')
        https_proxy = os.environ.get('https_proxy')
        return http_proxy, https_proxy

