        return jsonify({'message': 'Scan session not found'}), 404

    def api_stop(self, sid):
        for s in self.sessions:
            if s.id == sid:
                s.stop()
                return jsonify({})
        return jsonify({'message': 'Scan session not found'}), 404

    def run(self, host=HOST, port=PORT):
