class DNSTwistAPI:
    def __init__(self):
        self.app = Flask(__name__)
        self.sessions = []
        self._setup_routes()
        self._start_janitor()

    def _setup_routes(self):
        self.app.add_url_rule('/', 'root', self.root, methods=['GET'])
        self.app.add_url_rule('/api/scan', 'api_scan', self.api_scan, methods=['POST'])
        self.app.add_url_rule('/api/status/<sid>', 'api_status', self.api_status, methods=['GET'])
        self.app.add_url_rule('/api/domains/<sid>', 'api_domains', self.api_domains, methods=['GET'])
        self.app.add_url_rule('/api/csv/<sid>', 'api_csv', self.api_csv, methods=['GET'])
        self.app.add_url_rule('/api/json/<sid>', 'api_json', self.api_json, methods=['GET'])
        self.app.add_url_rule('/api/list/<sid>', 'api_list', self.api_list, methods=['GET'])
        self.app.add_url_rule('/api/stop/<sid>', 'api_stop', self.api_stop, methods=['POST'])

    def _start_janitor(self):
        threading.Thread(target=self.janitor, args=(self.sessions,), daemon=True).start()

    def janitor(self, sessions):
        while True:
            time.sleep(60)
            for session in sessions:
                if session['status'] == 'completed' or time.time() - session['start_time'] > 3600:
                    sessions.remove(session)

    def root(self):
        return Response("DNSTwist API", mimetype='text/plain')

    def api_scan(self):
        domain = request.json.get('domain')
        if not domain:
            return jsonify({'error': 'Domain is required'}), 400
        sid = str(len(self.sessions) + 1)
        session = {'id': sid, 'domain': domain, 'status': 'running', 'start_time': time.time(), 'results': None}
        self.sessions.append(session)
        threading.Thread(target=self._run_scan, args=(session,), daemon=True).start()
        return jsonify({'session_id': sid}), 202

    def _run_scan(self, session):
        domain = session['domain']
        dnstwist_obj = dnstwist.DNSTwist(domain)
        dnstwist_obj.run()
        session['results'] = dnstwist_obj.get_results()
        session['status'] = 'completed'

    def api_status(self, sid):
        session = next((s for s in self.sessions if s['id'] == sid), None)
        if not session:
            return jsonify({'error': 'Session not found'}), 404
        return jsonify({'status': session['status']})

    def api_domains(self, sid):
        session = next((s for s in self.sessions if s['id'] == sid), None)
        if not session or session['status'] != 'completed':
            return jsonify({'error': 'Session not found or not completed'}), 404
        domains = [result['domain-name'] for result in session['results'] if result['fuzzer'] == 'registered']
        return jsonify({'domains': domains})

    def api_csv(self, sid):
        session = next((s for s in self.sessions if s['id'] == sid), None)
        if not session or session['status'] != 'completed':
            return jsonify({'error': 'Session not found or not completed'}), 404
        csv_data = "domain-name,fuzzer\n" + "\n".join(
            f"{result['domain-name']},{result['fuzzer']}" for result in session['results']
        )
        return csv_data, 200, {'Content-Type': 'text/csv'}

    def api_json(self, sid):
        session = next((s for s in self.sessions if s['id'] == sid), None)
        if not session or session['status'] != 'completed':
            return jsonify({'error': 'Session not found or not completed'}), 404
        return jsonify(session['results'])

    def api_list(self, sid):
        session = next((s for s in self.sessions if s['id'] == sid), None)
        if not session or session['status'] != 'completed':
            return jsonify({'error': 'Session not found or not completed'}), 404
        list_data = "\n".join(result['domain-name'] for result in session['results'])
        return list_data, 200, {'Content-Type': 'text/plain'}

    def api_stop(self, sid):
        session = next((s for s in self.sessions if s['id'] == sid), None)
        if not session:
            return jsonify({'error': 'Session not found'}), 404
        session['status'] = 'stopped'
        return jsonify({'status': 'stopped'})

    def run(self, host='0.0.0.0', port=5000):
        self.app.run(host=host, port=port)

if __name__ == '__main__':
    api = DNSTwistAPI()
    api.run()

