import http.client
import urllib.parse

def perform_http_request(method, action, param=None, **params):
    logging.basicConfig(level=logging.DEBUG)
    logger = logging.getLogger(__name__)

    if param is None:
        param = {}

    param.update(params)
    url_parts = urllib.parse.urlparse(action)
    scheme = url_parts.scheme
    host = url_parts.hostname
    port = url_parts.port
    path = url_parts.path

    if scheme not in ['http', 'https']:
        raise ValueError("Unsupported URL scheme: {}".format(scheme))

    if scheme == 'http':
        conn = http.client.HTTPConnection(host, port)
    else:
        conn = http.client.HTTPSConnection(host, port)

    try:
        if method.upper() == 'GET':
            if param:
                path += '?' + urllib.parse.urlencode(param)
            logger.debug("GET request to %s", path)
            conn.request('GET', path)
        elif method.upper() == 'POST':
            headers = {'Content-type': 'application/x-www-form-urlencoded'}
            body = urllib.parse.urlencode(param)
            logger.debug("POST request to %s with body %s", path, body)
            conn.request('POST', path, body, headers)
        else:
            raise ValueError("Unsupported HTTP method: {}".format(method))

        response = conn.getresponse()
        logger.debug("Response status: %s", response.status)
        if 200 <= response.status < 300:
            response_body = response.read().decode('utf-8')
            logger.debug("Response body: %s", response_body)
            return response_body
        else:
            raise Exception("HTTP request failed with status: {}".format(response.status))
    except Exception as e:
        logger.error("Error during HTTP request: %s", str(e))
        raise
    finally:
        conn.close()

