import json
from google.cloud import bigquery
import logging

app = Flask(__name__)
client = bigquery.Client()

@app.route('/', methods=['POST'])
def pubsub_push():
    try:
        envelope = request.get_json()
        if not envelope or 'message' not in envelope:
            raise ValueError("Invalid Pub/Sub message format")

        message = envelope['message']
        attributes = message.get('attributes', {})
        data = message.get('data')

        if data:
            decoded_data = base64.b64decode(data).decode('utf-8')
            data_json = json.loads(decoded_data)
        else:
            data_json = {}

        build_id = attributes.get('buildId')
        if build_id:
            data_json['buildId'] = build_id

        table_id = "your_project.your_dataset.your_table"
        rows_to_insert = [data_json]

        errors = client.insert_rows_json(table_id, rows_to_insert)
        if errors:
            logging.error(f"Failed to insert rows: {errors}")
            raise RuntimeError("Failed to insert data into BigQuery")

        return '', 204

    except Exception as e:
        logging.error(f"Error processing Pub/Sub message: {str(e)}")
        return jsonify({'error': str(e)}), 400

