class ExchangeRateAPI:
    def __init__(self):
        self.app = app
        self.db = db
        self.config = {
            'DB_DSN': DATABASE_URL
        }
        self._setup_routes()

    def _setup_routes(self):
        self.app.add_route(self.exchange_rates, '/exchange_rates', methods=['GET'])
        self.app.add_route(self.exchange_rates, '/exchange_rates/<date>', methods=['GET'])

    async def exchange_rates(self, request: Request, date: str = None):
        base = request.args.get('base', 'USD')
        symbols = request.args.get('symbols')
        if date:
            try:
                date_obj = datetime.strptime(date, '%Y-%m-%d').date()
            except ValueError:
                return response.json({'error': 'Invalid date format'}, status=400)
        else:
            date_obj = datetime.utcnow().date()

        query = ExchangeRates.query.where(ExchangeRates.date == date_obj)
        result = await query.gino.first()

        if not result:
            return response.json({'error': 'No data available for the given date'}, status=404)

        rates = result.rates
        if base not in rates:
            return response.json({'error': 'Base currency not found'}, status=400)

        base_rate = rates[base]
        converted_rates = {currency: rate / base_rate for currency, rate in rates.items()}

        if symbols:
            symbols_list = symbols.split(',')
            converted_rates = {currency: rate for currency, rate in converted_rates.items() if currency in symbols_list}

        return response.json({
            'base': base,
            'date': date_obj.isoformat(),
            'rates': converted_rates
        })

    def run(self, host: str = "0.0.0.0", port: int = 8000):
        self.app.run(host=host, port=port)

