from urllib.parse import unquote
from io import BytesIO

class HTTPTestHandler(BaseHTTPRequestHandler):
    static_files = {}
    dynamic_files = {}
    files = ChainMap(static_files, dynamic_files)

    def _respond(self, code=200, headers=None, data=b""):
        self.send_response(code)
        if headers:
            for key, value in headers.items():
                self.send_header(key, value)
        self.end_headers()
        self.wfile.write(data)

    def do_GET(self):
        path = unquote(self.path)
        if path in self.files:
            content = self.files[path]
            headers = {"Content-Length": str(len(content))}
            if "Range" in self.headers:
                range_header = self.headers["Range"]
                start, end = self._parse_range(range_header, len(content))
                content = content[start:end]
                headers["Content-Range"] = f"bytes {start}-{end-1}/{len(content)}"
                self._respond(206, headers, content)
            else:
                self._respond(200, headers, content)
        else:
            self._respond(404, data=b"File not found")

    def do_POST(self):
        path = unquote(self.path)
        length = int(self.headers.get('Content-Length', 0))
        data = self.rfile.read(length)
        self.dynamic_files[path] = data
        self._respond(201, data=b"File created")

    def _parse_range(self, range_header, content_length):
        _, range_spec = range_header.split("=")
        start, end = range_spec.split("-")
        start = int(start) if start else 0
        end = int(end) if end else content_length
        return start, end

    def read_chunks(self):
        while True:
            length_str = self.rfile.readline().strip()
            length = int(length_str, 16)
            if length == 0:
                break
            yield self.rfile.read(length)
            self.rfile.readline()

    @contextmanager
    def serve():
        server = HTTPServer(('localhost', 0), HTTPTestHandler)
        url = f"http://localhost:{server.server_port}"
        thread = threading.Thread(target=server.serve_forever)
        thread.start()
        try:
            yield url
        finally:
            server.shutdown()
            thread.join()

