import json

class JSONEchoHandler(tornado.web.RequestHandler):
    def get(self):
        response = {"method": "GET", "data": self.request.arguments}
        self.write(json.dumps(response))

    def post(self):
        try:
            data = json.loads(self.request.body)
        except json.JSONDecodeError:
            data = {"error": "Invalid JSON"}
        response = {"method": "POST", "data": data}
        self.write(json.dumps(response))

class JSONEchoServer(threading.Thread):
    def __init__(self, lock, port, *args, **kw):
        super().__init__(*args, **kw)
        self.lock = lock
        self.port = port
        self._stop = threading.Event()
        self.daemon = True

    def stop(self):
        self._stop.set()

    def stopped(self):
        return self._stop.is_set()

    def setup_application(self):
        return tornado.web.Application([
            (r"/echo", JSONEchoHandler),
        ])

    def run(self):
        with self.lock:
            app = self.setup_application()
            app.listen(self.port)
            io_loop = tornado.ioloop.IOLoop.current()
            io_loop.add_callback(self._check_stop)
            io_loop.start()

    def _check_stop(self):
        if self.stopped():
            tornado.ioloop.IOLoop.current().stop()
        else:
            tornado.ioloop.IOLoop.current().add_timeout(
                tornado.ioloop.time.time() + 1, self._check_stop
            )

