            print("Producer not connected.")
            return False
        try:
            self._producer.send(
                topic_name,
                key=key.encode('utf-8'),
                value=value.encode('utf-8')
            )
            self._producer.flush()
            return True
        except KafkaError as e:
            print(f"Failed to publish message: {e}")
            return False

    def close(self):
        if self._producer:
            self._producer.close()
            self._producer = None

