import json
import re
from typing import Union, List, Any, Optional, Callable, Tuple
from http.client import HTTPConnection, HTTPSConnection
from urllib.parse import urlparse

class JSONRPCExecutor:
    def __init__(self, command: Union[str, List[str]], url: str, jsonrpc_method: str,
                 jsonrpc_params: Optional[List[Any]] = None, status: str = "^2\d\d$",
                 result_validator: Optional[Callable[[Any], Tuple[bool, Optional[str]]]] = None,
                 verify_tls: bool = True, method: str = "POST"):
        self.command = command
        self.url = url
        self.jsonrpc_method = jsonrpc_method
        self.jsonrpc_params = jsonrpc_params if jsonrpc_params is not None else []
        self.status = status
        self.result_validator = result_validator
        self.verify_tls = verify_tls
        self.method = method

    def _send_request(self, conn: Union[HTTPConnection, HTTPSConnection]) -> None:
        headers = {"Content-Type": "application/json"}
        payload = {
            "jsonrpc": "2.0",
            "method": self.jsonrpc_method,
            "params": self.jsonrpc_params,
            "id": 1
        }
        conn.request(self.method, urlparse(self.url).path, body=json.dumps(payload), headers=headers)

    def _validate_response(self, response) -> bool:
        if not re.match(self.status, str(response.status)):
            return False
        try:
            response_data = json.loads(response.read().decode())
            if "error" in response_data:
                return False
            if self.result_validator:
                valid, _ = self.result_validator(response_data.get("result"))
                return valid
            return True
        except json.JSONDecodeError:
            return False

    def execute(self) -> bool:
        parsed_url = urlparse(self.url)
        conn_class = HTTPSConnection if parsed_url.scheme == "https" else HTTPConnection
        conn = conn_class(parsed_url.hostname, parsed_url.port, context=None if self.verify_tls else False)
        try:
            self._send_request(conn)
            response = conn.getresponse()
            return self._validate_response(response)
        finally:
            conn.close()

