import concurrent.futures

class DefaultProducer(KafkaProducer):
    def send(self, topic: str, *, key: Optional[bytes] = None, value: Optional[bytes] = None) -> concurrent.futures.Future:
        future = concurrent.futures.Future()

        def on_send_success(record_metadata):
            future.set_result(record_metadata)

        def on_send_error(excp):
            future.set_exception(excp)

        super().send(topic, key=key, value=value).add_callback(on_send_success).add_errback(on_send_error)
        return future

    def __enter__(self) -> 'DefaultProducer':
        return self

    def __exit__(self, exc_type, exc_val, exc_tb) -> None:
        self.close()

