import subprocess

def inspect_python_object(pid, address):
    if isinstance(address, int):
        address = hex(address)

    gdb_command = f"gdb -q --batch -ex 'attach {pid}' -ex 'x/s {address}' -ex 'detach' -ex 'quit'"

    process = subprocess.Popen(gdb_command, shell=True, stdout=subprocess.PIPE, stderr=subprocess.PIPE)
    stdout, stderr = process.communicate()

    if process.returncode != 0:
        return None

    output_lines = stdout.decode().splitlines()
    for line in output_lines:
        if address in line:
            return line.split(':', 1)[1].strip()

    return None

