        super().__init__(ip, port)
        self.source = source
        self.target = target
        self.outputcls = outputcls

    def send(self, x: Any) -> int:
        data = self._pack_message(x)
        return super().send(data)

    def recv(self, x: int = 65535) -> Optional[Any]:
        data = super().recv(x)
        if data is None:
            return None
        return self._unpack_message(data)

    def _pack_message(self, x: Any) -> bytes:
        payload = x if isinstance(x, bytes) else str(x).encode()
        length = len(payload)
        header = struct.pack('!IHH', length, self.source, self.target)
        return header + payload

    def _unpack_message(self, data: bytes) -> Any:
        length, source, target = struct.unpack('!IHH', data[:8])
        payload = data[8:8 + length]
        if self.outputcls:
            return self.outputcls(payload)
        return payload

