            self.__csock.settimeout(timeout)
        elif nonblocking:
            self.__csock.setblocking(0)
        else:
            self.__csock.settimeout(self.__deftout)

        serialized_msg = self.CSPROTO_START + self.pickle.dumps(self.convert(msg)) + self.CSPROTO_END
        self.__csock.sendall(serialized_msg)
        return self.receive(self.__csock, nonblocking, timeout)

    def settimeout(self, timeout: float) -> None:
        self.__deftout = timeout if timeout >= 0 else None
        self.__csock.settimeout(self.__deftout)

