        self._id = self._acquire_uid()
        self._name = None
        self._sock = self._connect(addr)
        self._dup_sock = None
        self._finalizer = weakref.finalize(self, self._cleanup)

    def _acquire_uid(self) -> int:
        with self._lock:
            uid = self._n[0]
            self._n[0] += 1
            self._id_numbers.append(uid)
            return uid

    def _connect(self, addr: Union[str, Tuple[str, int], socket.socket, Any]) -> socket.socket:
        if isinstance(addr, socket.socket):
            return addr
        sock = socket.socket(socket.AF_INET if isinstance(addr, tuple) else socket.AF_UNIX, socket.SOCK_STREAM)
        sock.connect(addr)
        return sock

    def _cleanup(self) -> None:
        self.close()

    def _release_uid(self, id: int) -> None:
        with self._lock:
            self._id_numbers.remove(id)

    def close(self) -> None:
        if not self.closed:
            self._sock.close()
            self._release_uid(self._id)

    @property
    def closed(self) -> bool:
        return self._sock.fileno() == -1

    @property
    def id(self) -> int:
        return self._id

    @property
    def name(self) -> str:
        return self._name

    @name.setter
    def name(self, new_name: str) -> None:
        self._name = new_name

