import threading
from queue import Queue
from typing import Tuple

class BaseSocket:
    def __init__(self, host: str, port: int, debug: bool = False) -> None:
        self.serversocket = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
        self.serversocket.bind((host, port))
        self.serversocket.listen(5)
        self.queue = Queue()
        self.debug = debug
        self.disconnect = False
        self.t = threading.Thread(target=self.run)
        self.t.start()

    @staticmethod
    def recvLine(sock: socket.socket) -> Tuple[bool, str]:
        data = []
        while True:
            char = sock.recv(1).decode('utf-8')
            if char == '\n':
                break
            if not char:
                return False, ''
            data.append(char)
        return True, ''.join(data)

    @staticmethod
    def recvAll(sock: socket.socket, l: int) -> str:
        data = []
        while l > 0:
            chunk = sock.recv(l)
            if not chunk:
                break
            data.append(chunk.decode('utf-8'))
            l -= len(chunk)
        return ''.join(data)

    def run(self) -> None:
        while not self.disconnect:
            clientsocket, _ = self.serversocket.accept()
            threading.Thread(target=self.handle_client, args=(clientsocket,)).start()

    def handle_client(self, clientsocket: socket.socket) -> None:
        while not self.disconnect:
            success, line = self.recvLine(clientsocket)
            if not success:
                break
            length = int(line)
            data = self.recvAll(clientsocket, length)
            self.queue.put(data)
        clientsocket.close()

    def update(self) -> None:
        while not self.queue.empty():
            data = self.queue.get()
            self.main(data)

    def main(self, data: str) -> None:
        if self.debug:
            print(f"Received data: {data}")

    def __del__(self) -> None:
        self.disconnect = True
        self.serversocket.close()
        self.t.join()

