            readable, _, exceptional = select.select(self.input_list, [], self.input_list, self.DELAY)
            for s in readable:
                if s is self._server:
                    self.on_accept()
                else:
                    self.on_recv(s)
            for s in exceptional:
                self.on_close(s)

    def on_accept(self):
        client_sock, client_addr = self._server.accept()
        print(f"Connection from {client_addr}")
        try:
            device_sock = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
            device_sock.connect((self._rdev.host, self._rport))
            self.input_list.append(client_sock)
            self.input_list.append(device_sock)
            self.channel[client_sock] = device_sock
            self.channel[device_sock] = client_sock
        except Exception as e:
            print(f"Failed to connect to remote device: {e}")
            client_sock.close()

    def on_close(self, sock):
        peer = self.channel.get(sock)
        if peer:
            self.input_list.remove(peer)
            peer.close()
            del self.channel[peer]
        if sock in self.input_list:
            self.input_list.remove(sock)
            sock.close()
            del self.channel[sock]

    def on_recv(self, sock):
        data = sock.recv(self.BUFFER_SIZE)
        if data:
            self.channel[sock].send(data)
        else:
            self.on_close(sock)

