class Scheduler:
    def __init__(self):
        self.selector = selectors.DefaultSelector()

    def register(self, sock, event, task):
        self.selector.register(sock, event, self._ready(task))

    def _ready(self, task):
        def callback(key, mask):
            self.selector.unregister(key.fileobj)
            task.send(None)
        return callback

    def run(self):
        while True:
            events = self.selector.select()
            for key, mask in events:
                callback = key.data
                callback(key, mask)

