class TCPRequestHandler(socketserver.BaseRequestHandler):
    def get_data(self) -> bytes:
        try:
            raw_size = self.request.recv(2)
            if len(raw_size) < 2:
                raise ValueError("Incomplete size prefix")
            size = struct.unpack('!H', raw_size)[0]
            data = self.request.recv(size)
            if len(data) < size:
                raise ValueError("Incomplete data received")
            return data
        except Exception as e:
            print(f"Error receiving data: {e}")
            return b''

    def send_data(self, data: bytes) -> None:
        try:
            size = struct.pack('!H', len(data))
            self.request.sendall(size + data)
        except Exception as e:
            print(f"Error sending data: {e}")

    def handle(self) -> None:
        data = self.get_data()
        if data:
            print(f"Received data from {self.client_address}: {data}")
            response = b"Response to: " + data
            self.send_data(response)

