import zmq
import numpy as np
import struct
import time
from typing import Optional, Generator, Union, List

class BertClient:
    def __init__(self, ip='localhost', port=5555, port_out=5556, identity=None, timeout=-1):
        self.context = zmq.Context()
        self.sender = self.context.socket(zmq.PUSH)
        self.sender.connect(f'tcp://{ip}:{port}')
        self.receiver = self.context.socket(zmq.SUB)
        self.receiver.connect(f'tcp://{ip}:{port_out}')
        self.identity = identity or b'client-%d' % id(self)
        self.receiver.setsockopt(zmq.SUBSCRIBE, self.identity)
        self.request_id = 0
        self.timeout = timeout
        self.pending_request = set()

    def close(self):
        self.sender.close()
        self.receiver.close()
        self.context.term()

    def _send(self, msg, msg_len=0):
        self.request_id += 1
        self.pending_request.add(self.request_id)
        self.sender.send_multipart([self.identity, struct.pack('!I', self.request_id), msg])

    def _recv(self):
        if self.timeout > 0:
            if not self.receiver.poll(self.timeout * 1000):
                raise TimeoutError("Timeout while waiting for response")
        identity, req_id, msg = self.receiver.recv_multipart()
        req_id = struct.unpack('!I', req_id)[0]
        if req_id in self.pending_request:
            self.pending_request.remove(req_id)
            return msg
        return None

    def _recv_ndarray(self):
        msg = self._recv()
        if msg:
            return np.frombuffer(msg, dtype=np.float32)
        return None

    def encode(self, texts, blocking=True) -> Optional[np.ndarray]:
        self._send(texts.encode('utf-8'))
        if blocking:
            return self._recv_ndarray()
        return None

    def fetch(self, delay=0.0) -> Generator[np.ndarray, None, None]:
        while self.pending_request:
            if delay > 0:
                time.sleep(delay)
            result = self._recv_ndarray()
            if result is not None:
                yield result

    def fetch_all(self, sort=True, concat=False) -> Union[List[np.ndarray], np.ndarray]:
        results = list(self.fetch())
        if sort:
            results.sort(key=lambda x: x[0])
        if concat:
            return np.concatenate(results, axis=0)
        return results

