from threading import Thread

class NetGear:
    def __init__(self, address=None, port=None, protocol='tcp', pattern=2, receive_mode=False, logging=False, **options):
        self.__logging = logging
        self.__msg_context = zmq.Context()
        self.__receive_mode = receive_mode
        self.__pattern = pattern
        self.__terminate = False
        self.__subscriber_timeout = options.get('subscriber_timeout', 5)
        self.__queue = deque()
        self.__thread = None

        if self.__pattern == 2:  # PUB/SUB pattern
            if self.__receive_mode:
                self.__msg_socket = self.__msg_context.socket(zmq.SUB)
                self.__msg_socket.setsockopt_string(zmq.SUBSCRIBE, '')
                self.__msg_socket.setsockopt(zmq.RCVTIMEO, self.__subscriber_timeout * 1000)
                self.__msg_socket.connect(f"{protocol}://{address}:{port}")
                self.__thread = Thread(target=self.__recv_handler)
                self.__thread.start()
            else:
                self.__msg_socket = self.__msg_context.socket(zmq.PUB)
                self.__msg_socket.bind(f"{protocol}://{address}:{port}")

    def __recv_handler(self):
        while not self.__terminate:
            try:
                message = self.__msg_socket.recv()
                frame = pickle.loads(zlib.decompress(message))
                self.__queue.append(frame)
            except zmq.error.Again:
                continue

    def recv(self):
        if self.__receive_mode and self.__queue:
            return self.__queue.popleft()
        return None

    def send(self, frame, message=None):
        if not self.__receive_mode:
            serialized_frame = pickle.dumps(frame)
            compressed_frame = zlib.compress(serialized_frame)
            self.__msg_socket.send(compressed_frame)

    def close(self, kill=False):
        self.__terminate = True
        if self.__thread and self.__thread.is_alive():
            self.__thread.join()
        self.__msg_socket.close()
        self.__msg_context.term()
        if kill:
            self.__msg_context.destroy()

