            context.set_code(grpc.StatusCode.NOT_FOUND)
            context.set_details('Unknown endpoint')
            return bosdyn.api.estop_pb2.EstopCheckInResponse()

        if request.status in self.STATUSES_THAT_DO_NOT_PROVIDE_CHALLENGE:
            return bosdyn.api.estop_pb2.EstopCheckInResponse(challenge=self._challenge)

        if request.challenge != self._challenge:
            context.set_code(grpc.StatusCode.INVALID_ARGUMENT)
            context.set_details('Invalid challenge response')
