import hmac
import hashlib
import json
from typing import Dict, Any
import httpx

class ExternalActionResponse:
    def __init__(self, success: bool, data: Any = None, error: str = None):
        self.success = success
        self.data = data
        self.error = error

class ExternalActionsRequester:
    def __init__(self, url: str) -> None:
        self.url = url

    async def send_request(
        self,
        payload: Dict[str, Any],
        signature_secret: str,
        additional_payload_values: Dict[str, Any] = {},
        additional_headers: Dict[str, str] = {},
        transport: httpx.AsyncHTTPTransport = httpx.AsyncHTTPTransport(retries=2)
    ) -> ExternalActionResponse:
        async with httpx.AsyncClient(transport=transport) as client:
            try:
                payload.update(additional_payload_values)
                payload_bytes = json.dumps(payload).encode('utf-8')
                signature = self._encode_payload(payload_bytes, signature_secret)
                headers = {'Content-Type': 'application/json', 'X-Signature': signature}
                headers.update(additional_headers)
                response = await client.post(self.url, content=payload_bytes, headers=headers)
                response_data = response.json()
                return self._validate_response(response_data)
            except httpx.HTTPStatusError as e:
                return ExternalActionResponse(success=False, error=f"HTTP error: {str(e)}")
            except Exception as e:
                return ExternalActionResponse(success=False, error=f"Unexpected error: {str(e)}")

    def _encode_payload(self, payload: bytes, signature_secret: str) -> str:
        return hmac.new(signature_secret.encode('utf-8'), payload, hashlib.sha256).hexdigest()

    def _validate_response(self, response: Dict[str, Any]) -> ExternalActionResponse:
        if 'success' in response and response['success']:
            return ExternalActionResponse(success=True, data=response.get('data'))
        return ExternalActionResponse(success=False, error=response.get('error', 'Unknown error'))

