import aiohttp

class Api:
    def __init__(self, api_url=None, create_response_files=True):
        self._api_url = api_url or "http://127.0.0.1:8000"
        self._create_response_files = create_response_files

    def is_api_running(self):
        try:
            response = requests.get(self._make_api_url('/'))
            self._raise_for_status(response)
            return True
        except requests.RequestException:
            return False

    def create_async_task(self, data, scraper_name=None):
        return asyncio.run(self._create_async_task(data, scraper_name))

    async def _create_async_task(self, data, scraper_name=None):
        async with aiohttp.ClientSession() as session:
            try:
                url = self._make_api_url('/async-task')
                async with session.post(url, json=data) as response:
                    await self._raise_for_status_async(response)
                    return await response.json()
            except aiohttp.ClientError as e:
                return {'error': str(e)}

    def create_sync_task(self, data, scraper_name=None):
        try:
            url = self._make_api_url('/sync-task')
            response = requests.post(url, json=data)
            self._raise_for_status(response)
            return response.json()
        except requests.RequestException as e:
            return {'error': str(e)}

    def _make_api_url(self, path):
        return f"{self._api_url}{path}"

    def _raise_for_status(self, response):
        if response.status_code >= 400:
            raise requests.HTTPError(f"HTTP error {response.status_code}: {response.text}")

    async def _raise_for_status_async(self, response):
        if response.status >= 400:
            text = await response.text()
            raise aiohttp.ClientResponseError(
                status=response.status,
                message=f"HTTP error {response.status}: {text}"
            )

