import requests

class AccuWeatherClient:
    def __init__(self, api_key: str = "") -> None:
        self.api_key = api_key
        self.base_url = "http://dataservice.accuweather.com"

    def get_location_key(self, city: str) -> str:
        if not self.api_key:
            return "Error: API key is required."
        url = f"{self.base_url}/locations/v1/cities/search"
        params = {"apikey": self.api_key, "q": city}
        response = requests.get(url, params=params)
        if response.status_code == 200:
            data = response.json()
            if data:
                return data[0]["Key"]
            else:
                return "Error: City not found."
        else:
            return f"Error: Unable to fetch location key. Status code: {response.status_code}"

    def get_weather(self, city: str, extensions: str = "all") -> str:
        location_key = self.get_location_key(city)
        if "Error" in location_key:
            return location_key
        if extensions == "all":
            url = f"{self.base_url}/currentconditions/v1/{location_key}"
        elif extensions == "base":
            url = f"{self.base_url}/forecasts/v1/daily/5day/{location_key}"
        else:
            return "Error: Invalid extensions parameter."
        params = {"apikey": self.api_key}
        response = requests.get(url, params=params)
        if response.status_code == 200:
            data = response.json()
            return str(data)
        else:
            return f"Error: Unable to fetch weather data. Status code: {response.status_code}"

