    current_version = cfg['current_version']
    include_prereleases = cfg['include_prereleases']
    user_agent = cfg['user_agent']

    headers = {'User-Agent': user_agent}
    response = requests.get(f'https://api.github.com/repos/{repo_url}/releases', headers=headers)

    if response.status_code != 200:
        logging.error('Failed to fetch releases from GitHub API')
        return

    releases = response.json()
    latest_version = None

    for release in releases:
        if not include_prereleases and release['prerelease']:
            continue

        release_version = release['tag_name']
        if latest_version is None or version.parse(release_version) > version.parse(latest_version):
            latest_version = release_version

    if latest_version and version.parse(latest_version) > version.parse(current_version):
        logging.info(f'New version available: {latest_version}')
        notif.send(f'Update available: {latest_version}')
