import requests
import concurrent.futures

def query_dns(domain, record_type):
    google_dns_url = f"https://dns.google/resolve?name={domain}&type={record_type}"
    cloudflare_dns_url = f"https://cloudflare-dns.com/dns-query?name={domain}&type={record_type}"
    headers = {"Accept": "application/dns-json"}

    def fetch_dns(url):
        try:
            response = requests.get(url, headers=headers)
            if response.status_code == 200:
                return response.json()
        except requests.RequestException:
            pass
        return None

    with concurrent.futures.ThreadPoolExecutor() as executor:
        google_future = executor.submit(fetch_dns, google_dns_url)
        cloudflare_future = executor.submit(fetch_dns, cloudflare_dns_url)
        google_response = google_future.result()
        cloudflare_response = cloudflare_future.result()

    return [google_response, cloudflare_response]

