import socket
from queue import LifoQueue
from http.client import HTTPConnection, HTTPResponse
from urllib3.util.retry import Retry
from urllib3.util.timeout import Timeout

class HTTPConnectionPool:
    def __init__(self, host, port=None, strict=False, timeout=Timeout.DEFAULT_TIMEOUT, maxsize=1, block=False, headers=None, retries=None, _proxy=None, _proxy_headers=None, **conn_kw):
        self.scheme = 'http'
        self.ConnectionCls = HTTPConnection
        self.host = host
        self.port = port or 80
        self.timeout = timeout
        self.retries = retries or Retry()
        self.pool = LifoQueue(maxsize)
        self.block = block
        self.proxy = _proxy
        self.proxy_headers = _proxy_headers or {}
        self.conn_kw = conn_kw
        for _ in range(maxsize):
            self.pool.put(None)

    def _new_conn(self):
        conn = self.ConnectionCls(self.host, self.port, **self.conn_kw)
        conn.timeout = self.timeout.total
        return conn

    def _get_conn(self, timeout=None):
        try:
            conn = self.pool.get(block=self.block, timeout=timeout)
        except Exception:
            conn = None
        if conn is None:
            conn = self._new_conn()
        return conn

    def _put_conn(self, conn):
        try:
            self.pool.put(conn, block=self.block)
        except Exception:
            pass

    def _make_request(self, conn, method, url, timeout=Timeout.DEFAULT_TIMEOUT, **httplib_request_kw):
        conn.request(method, url, **httplib_request_kw)
        response = conn.getresponse()
        return response

    def urlopen(self, method, url, body=None, headers=None, retries=None, redirect=True, timeout=Timeout.DEFAULT_TIMEOUT, **response_kw):
        if retries is None:
            retries = self.retries
        conn = self._get_conn(timeout=timeout.total)
        try:
            response = self._make_request(conn, method, url, timeout=timeout, body=body, headers=headers, **response_kw)
            return response
        except (socket.error, HTTPException) as e:
            if retries.is_retry(method, response=None, error=e):
                retries = retries.increment(method, url, error=e)
                return self.urlopen(method, url, body, headers, retries, redirect, timeout, **response_kw)
            raise
        finally:
            self._put_conn(conn)

    def close(self):
        while not self.pool.empty():
            conn = self.pool.get()
            if conn:
                conn.close()

