from fastapi import FastAPI
from jina import Flow
import requests
import uvicorn
from threading import Thread

def test_extend_fastapi():
    app = FastAPI()

    @app.get("/hello")
    def read_root():
        return {"message": "Hello, World!"}

    def run_app():
        uvicorn.run(app, host="127.0.0.1", port=8000)

    thread = Thread(target=run_app)
    thread.start()

    try:
        response = requests.get("http://127.0.0.1:8000/hello")
        assert response.status_code == 200
        assert response.json() == {"message": "Hello, World!"}
    finally:
        thread.join()

test_extend_fastapi()

import requests
import base64

class AmazonSSO:
    def __init__(self, access_token=None, refresh_token=None, client_id='', client_secret='', user_pool_id='', region=''):
        self.access_token = access_token
        self.refresh_token = refresh_token
        self.client_id = client_id
        self.client_secret = client_secret
        self.user_pool_id = user_pool_id
        self.region = region
        self.user_info = {}

    def get_new_token(self):
        url = f'https://cognito-idp.{self.region}.amazonaws.com/{self.user_pool_id}/oauth2/token'
        headers = {
            'Content-Type': 'application/x-www-form-urlencoded',
            'Authorization': 'Basic ' + base64.b64encode(f'{self.client_id}:{self.client_secret}'.encode()).decode()
        }
        data = {
            'grant_type': 'refresh_token',
            'refresh_token': self.refresh_token
        }
        response = requests.post(url, headers=headers, data=data)
        if response.status_code == 200:
            self.access_token = response.json().get('access_token')
            return self.access_token
        return None

    def get_user_info(self):
        url = f'https://cognito-idp.{self.region}.amazonaws.com/{self.user_pool_id}/oauth2/userInfo'
        headers = {
            'Authorization': f'Bearer {self.access_token}'
        }
        response = requests.get(url, headers=headers)
        if response.status_code == 200:
            self.user_info = response.json()
            return self.user_info
        return None

    @staticmethod
    def amazon_sso(code, redirect_uri=None, client_id='', client_secret='', user_pool_id='', region=''):
        url = f'https://cognito-idp.{region}.amazonaws.com/{user_pool_id}/oauth2/token'
        headers = {
            'Content-Type': 'application/x-www-form-urlencoded',
            'Authorization': 'Basic ' + base64.b64encode(f'{client_id}:{client_secret}'.encode()).decode()
        }
        data = {
            'grant_type': 'authorization_code',
            'code': code,
            'redirect_uri': redirect_uri
        }
        response = requests.post(url, headers=headers, data=data)
        if response.status_code == 200:
            tokens = response.json()
            return AmazonSSO(
                access_token=tokens.get('access_token'),
                refresh_token=tokens.get('refresh_token'),
                client_id=client_id,
                client_secret=client_secret,
                user_pool_id=user_pool_id,
                region=region
            ), None
        return None, None

