import os
import requests

class LinkedInToolkit:
    def __init__(self) -> None:
        self._access_token = self._get_access_token()

    def _get_access_token(self) -> str:
        return os.getenv('LINKEDIN_ACCESS_TOKEN')

    def create_post(self, text: str) -> dict:
        url = 'https://api.linkedin.com/v2/shares'
        headers = {
            'Authorization': f'Bearer {self._access_token}',
            'Content-Type': 'application/json'
        }
        data = {
            'content': {
                'contentEntities': [],
                'title': text
            },
            'distribution': {
                'linkedInDistributionTarget': {}
            },
            'owner': 'urn:li:person:YOUR_PERSON_ID',
            'subject': text,
            'text': {
                'text': text
            }
        }
        response = requests.post(url, headers=headers, json=data)
        return response.json()

    def delete_post(self, post_id: str) -> str:
        url = f'https://api.linkedin.com/v2/shares/{post_id}'
        headers = {
            'Authorization': f'Bearer {self._access_token}'
        }
        response = requests.delete(url, headers=headers)
        return 'Post deleted' if response.status_code == 204 else 'Failed to delete post'

    def get_profile(self, include_id: bool = False) -> dict:
        url = 'https://api.linkedin.com/v2/me'
        headers = {
            'Authorization': f'Bearer {self._access_token}'
        }
        response = requests.get(url, headers=headers)
        profile_data = response.json()
        if not include_id:
            profile_data.pop('id', None)
        return profile_data
