import json
import os

def gentle_request(wav_path, txt_path, server_addr, port, debug=False):
    wav_name = os.path.basename(wav_path)
    txt_name = os.path.basename(txt_path)
    name = os.path.splitext(wav_name)[0]

    if name != os.path.splitext(txt_name)[0]:
        print("Error: Filenames do not match.")
        return None

    try:
        with open(txt_path, 'r') as f:
            transcript = f.read()
    except FileNotFoundError:
        print(f"Error: Transcript file not found: {txt_path}")
        return None
    except Exception as e:
        print(f"Error reading transcript: {e}")
        return None

    url = f"http://{server_addr}:{port}/transcriptions?async=false"
    files = {'audio': open(wav_path, 'rb'), 'transcript': transcript}

    try:
        response = requests.post(url, files=files)
        response.raise_for_status()  # Raise HTTPError for bad responses (4xx or 5xx)

        if debug:
            try:
                print(json.dumps(response.json(), indent=4))
            except json.JSONDecodeError:
                print("Response is not valid JSON:", response.text)

        return response

    except requests.exceptions.RequestException as e:
