    def delete_file_from_branch(self, repo_owner, repo_name, file_path, branch, access_token, commit_message="[comfyui_LLM_party] file deleted"):
        url = f"{self.url_prefix}/{repo_owner}/{repo_name}/contents/{file_path}"
        headers = {"Content-Type": "application/json;charset=UTF-8"}
        params = {"access_token": access_token, "ref": branch}

        try:
            response = requests.get(url, headers=headers, params=params)
            response.raise_for_status()
            sha = response.json().get("sha")

            if not sha:
                return ("File not found or SHA not retrieved",)

            data = {
                "access_token": access_token,
                "sha": sha,
                "branch": branch,
                "message": commit_message
            }

            response = requests.delete(url, headers=headers, params=data)
            response.raise_for_status()
            return ("Success",)

        except requests.exceptions.RequestException as e:
            try:
                error_message = e.response.json().get("message", str(e))
            except json.JSONDecodeError:
                error_message = str(e)
            return (error_message,)

