    def get_domain_id(self, domain):
        if domain in self.domain_list:
            return self.domain_list[domain]
        data = self.request('Domain.List')
        for each in data['domains']:
            self.domain_list[each['name']] = each['id']
        if domain in self.domain_list:
            return self.domain_list[domain]
        raise Exception(f'Domain {domain} not found')

    def get_records(self, did, **conditions):
        if did not in self.records:
            self.records[did] = {}
            offset = 0
            length = 100
            while True:
                data = self.request('Record.List', domain_id=did, offset=offset, length=length)
                if not data['records']:
                    break
                for record in data['records']:
                    self.records[did][record['id']] = {k: record[k] for k in self.record_keys if k in record}
                offset += length
        result = []
        for record in self.records[did].values():
            match = True
            for k, v in conditions.items():
                if k not in record or record[k] != v:
                    match = False
                    break
            if match:
                result.append(record)
        return result

    def update_record(self, domain, value, record_type="A"):
        did, sub = self.get_domain_info(domain)
        records = self.get_records(did, name=sub, type=record_type)
        if records:
            record = records[0]
            if record['value'] != value:
                data = self.request('Record.Modify', record_id=record['id'], domain_id=did,
                                    sub_domain=sub, record_type=record_type,
                                    record_line=self.DEFAULT, value=value, ttl=self.TTL or record['ttl'])
                self.records[did][record['id']]['value'] = value
                return data
        else:
            data = self.request('Record.Create', domain_id=did, sub_domain=sub,
                                record_type=record_type, record_line=self.DEFAULT,
                                value=value, ttl=self.TTL)
            if did in self.records:
                self.records[did][data['record']['id']] = {k: data['record'][k] for k in self.record_keys if k in data['record']}
            return data

