from typing import Optional, Tuple, Dict, Any

class SocketLibrary:
    def create_tcp_socket(self, host: str, port: int, timeout: float) -> Optional[Tuple[socket.socket, bool]]:
        try:
            socket_connection = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
            socket_connection.settimeout(timeout)
            socket_connection.connect((host, port))
            ssl_flag = False
        except ConnectionRefusedError:
            return None

        try:
            socket_connection = ssl.wrap_socket(socket_connection)
            ssl_flag = True
        except Exception:
            socket_connection = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
            socket_connection.settimeout(timeout)
            socket_connection.connect((host, port))

        return socket_connection, ssl_flag

    def tcp_connect_only(self, host: str, port: int, timeout: float) -> Optional[Dict[str, Any]]:
        tcp_socket = self.create_tcp_socket(host, port, timeout)
        if tcp_socket is None:
            return None

        socket_connection, ssl_flag = tcp_socket
        peer_name = socket_connection.getpeername()
        socket_connection.close()
        return {
            "peer_name": peer_name,
            "service": socket.getservbyport(int(port)),
            "ssl_flag": ssl_flag,
