import socket

class OpenVPNTCPClient:
    def __init__(self, host, port, timeout=10):
        self.host = host
        self.port = port
        self.timeout = timeout
        self.socket = None

    def connect(self):
        try:
            self.socket = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
            self.socket.settimeout(int(self.timeout))
            self.socket.connect((self.host, self.port))
            return True
        except socket.error as e:
            return False

    def send_command(self, command):
        try:
            self.socket.send(command + '\r\n')
            return True
        except socket.error as e:
            return False

    def receive_data(self):
        try:
            fd = self.socket.makefile('rb')
            lines = []
            while True:
                line = fd.readline()
                lines.append(line)
                if line.strip() == 'END':
                    break
            return lines
        except socket.error as e:
            return None

    def close(self):
        try:
            if self.socket:
                self.socket.close()
            return True
        except socket.error:
            return False

    def get_status(self):
        if not self.connect():
            return None

        if not self.send_command('status'):
            self.close()
            return None

        data = self.receive_data()
        self.close()

        return data

