        os.makedirs(self.state.buffer_dir, exist_ok=True)
        os.makedirs(self.state.drain_dir, exist_ok=True)

    def start_server(self, host: str, port: int):
        with socket.socket(socket.AF_INET, socket.SOCK_STREAM) as s:
            s.bind((host, port))
            s.listen()
            print(f"Parameter server started on {host}:{port}")
            while True:
                conn, addr = s.accept()
                threading.Thread(target=self.handle_client, args=(conn, addr)).start()

    def handle_client(self, conn: socket.socket, addr):
        with conn:
            print(f"Connected by {addr}")
            while True:
                data = conn.recv(1024 * 1024)  # Increased buffer size
                if not data:
                    break
                msg = json.loads(data.decode())
                if msg["type"] == "get_param":
                    self.handle_get_param(msg, conn)
                elif msg["type"] == "submit_replay":
                    self.handle_submit_replay(msg, conn)
                elif msg["type"] == "submit_param":
                    self.handle_submit_param(msg)
                elif msg["type"] == "drain":
                    self.handle_drain(conn)

    def handle_get_param(self, msg: dict, conn: socket.socket):
        with self.state.param_lock:
            param = self.state.mortal_param if msg["param_type"] == "mortal" else self.state.dqn_param
            if param:
                conn.sendall(json.dumps({"param": param, "version": self.state.param_version}).encode())
            else:
                conn.sendall(json.dumps({"param": None, "version": self.state.param_version}).encode())


    def handle_submit_replay(self, msg: dict, conn: socket.socket):
        with self.state.dir_lock:
            if self.state.buffer_size < self.state.capacity:
                replay_path = os.path.join(self.state.buffer_dir, f"replay_{self.state.submission_id}.json")
                with open(replay_path, 'w') as f:
                    json.dump(msg["replay"], f)
                self.state.buffer_size += 1
                self.state.submission_id += 1
                conn.sendall(b"success")
            else:
                conn.sendall(b"failed")

    def handle_submit_param(self, msg: dict):
        with self.state.param_lock:
            if self.state.force_sequential and msg["version"] != self.state.param_version + 1:
                return
            if msg["param_type"] == "mortal":
                self.state.mortal_param = msg["param"]
            else:
                self.state.dqn_param = msg["param"]
            self.state.param_version = msg["version"]

    def handle_drain(self, conn: socket.socket):
        with self.state.dir_lock:
            files = os.listdir(self.state.buffer_dir)
            if files:
                for file in files:
                    shutil.move(os.path.join(self.state.buffer_dir, file), self.state.drain_dir)
                self.state.buffer_size = 0
                conn.sendall(b"success")
            else:
