                dstip = (socket.inet_ntop(socket.AF_INET6, cmsg_data[8:]), int.from_bytes(cmsg_data[2:4], 'big'))
                break
        return addr, dstip, data


    def _recv_udp_socket_ext(self, listener: socket.socket, bufsize: int) -> Tuple[Any, Optional[Tuple[str, int]], bytes]:
        import socket_ext
        data, ancdata, addr = socket_ext.recvmsg_with_dest_addr(listener.fileno(), bufsize)
        dstip = ancdata
        return addr, dstip, data

    def _recv_udp_fallback(self, listener: socket.socket, bufsize: int) -> Tuple[Any, None, bytes]:
        data, addr = listener.recvfrom(bufsize)
        return addr, None, data

    def send_udp(self, sock: socket.socket, srcip: Optional[Tuple[str, int]], dstip: Tuple[str, int], data: bytes) -> None:
        if srcip is not None:
            sock.bind(srcip)
        sock.sendto(data, dstip)

    def setup_udp_listener(self, udp_listener: socket.socket) -> None:
        if hasattr(socket, "SO_REUSEPORT"):
            udp_listener.setsockopt(socket.SOL_SOCKET, socket.SO_REUSEPORT, 1)
        if self.recvmsg == 'python':
            udp_listener.setsockopt(socket.IPPROTO_IP, socket.IP_RECVORIGDSTADDR, 1)
            udp_listener.setsockopt(socket.IPPROTO_IPV6, socket.IPV6_RECVORIGDSTADDR, 1)
        elif self.recvmsg == 'socket_ext':
            pass  # No need to set socket options for socket_ext
        if os.name != 'nt':
            udp_listener.setsockopt(socket.SOL_IP, socket.IP_TRANSPARENT, 1)

