import stomp
import time
import threading

class MessageArtemisExtension:
    conns = []

    def __init__(self):
        self.conn = None


    class MyListener(stomp.ConnectionListener):
        def __init__(self, parent):
            self.parent = parent

        def on_error(self, headers, message):
            print(f'received an error "{message}"')

        def on_message(self, headers, message):
            print(f'received a message "{message}"')


    def listen_queue(self, profile=None):
        profile = profile or {}
        host = profile.get("host", "127.0.0.1")
        port = profile.get("port", 61616)
        username = profile.get("username", "artemis")
        password = profile.get("password", "artemis")
        destination = profile.get("destination", "arkid")

        self.conn = stomp.Connection([(host, port)])
        self.conn.set_listener('', self.MyListener(self))
        self.conns.append(self.conn)
        self.conn.start()
        self.conn.connect(username, password, wait=True)
        self.conn.subscribe(destination=destination, id=1, ack='auto')

        while True:
            time.sleep(1)


    def disconnect(self):
        if self.conn:
            try:
                self.conn.disconnect()
                self.conns.remove(self.conn)
                self.conn = None
            except Exception as e:
                print(f"Error during disconnect: {e}")

