import requests
from fake_useragent import UserAgent
import random
import time

class HTTPClient:

    def __init__(self, proxy_host=None, proxy_port=None, proxy_user=None, proxy_pass=None):
        self.ua = UserAgent()
        self.proxyHost = proxy_host
        self.proxyPort = proxy_port
        self.proxyUser = proxy_user
        self.proxyPass = proxy_pass
        self.proxies = self.set_proxies()

    def set_proxies(self):
        if self.proxyHost and self.proxyPort:
            proxy_url = f"http://{self.proxyUser}:{self.proxyPass}@{self.proxyHost}:{self.proxyPort}" if self.proxyUser and self.proxyPass else f"http://{self.proxyHost}:{self.proxyPort}"
            return {"http": proxy_url, "https": proxy_url}
        return None

    def get_request(self, url, headers=None, params=None, timeout=4):
        for attempt in range(3):  # Retry up to 3 times
            try:
                default_headers = {"User-Agent": self.ua.random}
                if headers:
                    default_headers.update(headers)
                response = requests.get(url, headers=default_headers, params=params, proxies=self.proxies, timeout=timeout)
                if response.status_code == 200 and "invalid" not in response.text.lower(): # Basic validation
                    return response
            except requests.exceptions.RequestException as e:
                print(f"Request failed: {e}")
                time.sleep(1 + random.random()) # Wait before retrying
        return None

    def post_request(self, url, data=None, headers=None, timeout=4):
        for attempt in range(3):
            try:
                default_headers = {"User-Agent": self.ua.random}
                if headers:
                    default_headers.update(headers)
                response = requests.post(url, data=data, headers=default_headers, proxies=self.proxies, timeout=timeout)
                if response.status_code == 200 and "invalid" not in response.text.lower():
                    return response
            except requests.exceptions.RequestException as e:
                print(f"Request failed: {e}")
                time.sleep(1 + random.random())
        return None

