    ) -> None:
        self._pipeline = pipeline
        self.host = host
        self.port = port
        self.workers = workers

        self._app = FastAPI()

        self._app.get("/model_info", response_model=ServeModelInfoResult)(self.model_info)
        self._app.post("/tokenize", response_model=ServeTokenizeResult)(self.tokenize)
        self._app.post("/detokenize", response_model=ServeDeTokenizeResult)(self.detokenize)
        self._app.post("/forward", response_model=ServeForwardResult)(self.forward)

    def run(self) -> None:
        uvicorn.run(self._app, host=self.host, port=self.port, workers=self.workers)

    def model_info(self) -> ServeModelInfoResult:
        return ServeModelInfoResult(model_info=vars(self._pipeline.model.config))

    def tokenize(
        self, text_input: str = Body(None, embed=True), return_ids: bool = Body(False, embed=True)
    ) -> ServeTokenizeResult:
        tokens = self._pipeline.tokenizer.tokenize(text_input)
        token_ids = self._pipeline.tokenizer.convert_tokens_to_ids(tokens) if return_ids else []
        return ServeTokenizeResult(tokens=tokens, token_ids=token_ids)

    def detokenize(
        self,
        tokens_ids: List[int] = Body(None, embed=True),
        skip_special_tokens: bool = Body(False, embed=True),
        cleanup_tokenization_spaces: bool = Body(True, embed=True),
    ) -> ServeDeTokenizeResult:
        text = self._pipeline.tokenizer.decode(
            tokens_ids, skip_special_tokens, cleanup_tokenization_spaces
        )
        return ServeDeTokenizeResult(text=text)

    def forward(self, inputs=Body(None, embed=True)) -> ServeForwardResult:
        outputs = self._pipeline(**inputs)
        return ServeForwardResult(outputs=outputs)

