        user = request.get_json()
        user['id'] = self.next_user_id
        self.users[self.next_user_id] = user
        self.next_user_id += 1
        return jsonify(user), 201

    def update_user(self, user_id):
        if self.config.fail_user_update:
            return jsonify({'message': 'User update failed'}), 500
        if user_id not in self.users:
            return jsonify({'message': 'User not found'}), 404
        user = request.get_json()
        user['id'] = user_id
        self.users[user_id] = user
        return jsonify(user)

    def delete_user(self, user_id):
        if self.config.fail_user_delete:
            return jsonify({'message': 'User deletion failed'}), 500
        if user_id not in self.users:
            return jsonify({'message': 'User not found'}), 404
        del self.users[user_id]
        return jsonify({'message': 'User deleted'})

    def delete_order(self, order_id):
        if self.config.fail_order_delete:
            return jsonify({'message': 'Order deletion failed'}), 500
        self.deleted_orders.add(order_id)
        return jsonify({'message': 'Order deleted'})

