        self.login_manager.unauthorized_handler = self.unauthorized

        class User(UserMixin, self.db.Model):
            id = self.db.Column(self.db.Integer, primary_key=True)
            username = self.db.Column(self.db.String(80), unique=True, nullable=False)
            email = self.db.Column(self.db.String(120), unique=True, nullable=False)
            password = self.db.Column(self.db.String(128), nullable=False)
            role = self.db.Column(self.db.String(20), default='user')

            def __repr__(self):
                return f'<User {self.username}>'

        self.User = User

