            password = PasswordField('Password', validators=[DataRequired()])
            submit = SubmitField('Login')

        class RegistrationForm(FlaskForm):
            username = StringField('Username', validators=[DataRequired()])
            email = StringField('Email', validators=[DataRequired(), Email()])
            password = PasswordField('Password', validators=[DataRequired()])
            confirm_password = PasswordField('Confirm Password', validators=[DataRequired(), EqualTo('password')])
            submit = SubmitField('Register')

        class CaseForm(FlaskForm):
            case_name = StringField('Case Name', validators=[DataRequired()])
            submit = SubmitField('Create Case')


        self.SettingForm = SettingForm
        self.LoginForm = LoginForm
        self.RegistrationForm = RegistrationForm
        self.CaseForm = CaseForm

        self.db.create_all()
        self.add_routes()

    def run(self, host='0.0.0.0', port=8080):
        self.app.run(host=host, port=port)

    def login_required(self, role="ANY"):
        def wrapper(fn):
            @login_required
            def decorated_view(*args, **kwargs):
                if not current_user.is_authenticated or (role != "ANY" and current_user.role != role):
                    return self.unauthorized()
                return fn(*args, **kwargs)
            return decorated_view
        return wrapper

    @self.login_manager.user_loader
    def load_user(self, user_id):
        return self.User.query.get(int(user_id))

    def unauthorized(self):
