from flask import Flask, send_from_directory, request, redirect, url_for, render_template_string, Response
from werkzeug.security import generate_password_hash, check_password_hash
from functools import wraps
import os

def create_flask_app(directory, port, password, ssl):
    app = Flask(__name__)
    app.config['UPLOAD_FOLDER'] = directory
    password_hash = generate_password_hash(password)

    def check_auth(username, password):
        return check_password_hash(password_hash, password)

    def authenticate():
        return Response(
            'Could not verify your access level for that URL.\n'
            'You have to login with proper credentials', 401,
            {'WWW-Authenticate': 'Basic realm="Login Required"'}
        )

    def requires_auth(f):
        @wraps(f)
        def decorated(*args, **kwargs):
            auth = request.authorization
            if not auth or not check_auth(auth.username, auth.password):
                return authenticate()
            return f(*args, **kwargs)
        return decorated

    @app.route('/', defaults={'path': ''})
    @app.route('/<path:path>')
    @requires_auth
    def home(path):
        full_path = os.path.join(app.config['UPLOAD_FOLDER'], path)
        if os.path.exists(full_path):
            if os.path.isdir(full_path):
                try:
                    files = os.listdir(full_path)
                    file_list = []
                    for file in files:
                        file_path = os.path.join(path, file)
                        file_list.append({
                            'name': file,
                            'path': file_path,
                            'is_dir': os.path.isdir(os.path.join(full_path, file))
                        })
                    return render_template_string('''
                        <!DOCTYPE html>
                        <html>
                        <head><title>Directory Listing</title></head>
                        <body>
                        <h1>Directory Listing</h1>
                        <ul>
                        {% for file in files %}
                            <li><a href="{{ file.path }}">{{ file.name }}</a> {% if file.is_dir %}(directory){% endif %}</li>
                        {% endfor %}
                        </ul>
                        <form method="POST" enctype="multipart/form-data" action="/upload/{{ path }}">
                            <input type="file" name="file">
                            <input type="submit" value="Upload">
                        </form>
                        </body>
                        </html>
                    ''', files=file_list, path=path)

                except PermissionError:
                    return "Permission denied", 403
            else:
                return send_from_directory(app.config['UPLOAD_FOLDER'], path)
        else:
            return "File not found", 404

    @app.route('/upload/<path:path>', methods=['POST'])
    @requires_auth
    def upload(path):
        if 'file' not in request.files:
            return redirect(request.url)
        file = request.files['file']
        if file.filename == '':
            return redirect(request.url)
        try:
            full_path = os.path.join(app.config['UPLOAD_FOLDER'], path)
            if not os.path.exists(full_path):
                os.makedirs(full_path)
            file.save(os.path.join(full_path, file.filename))
            return redirect(url_for('home', path=path))
        except PermissionError:
            return "Permission denied", 403


    if ssl:
        app.run(ssl_context='adhoc', host='0.0.0.0', port=port, debug=True)
    else:
        app.run(host='0.0.0.0', port=port, debug=True)

