sessions = {}

@app.route('/')
def index():
    return render_template('index.html')

@app.route('/get_qrcode', methods=['GET'])
def get_qrcode():
    sid = secrets.token_urlsafe(16)
    sessions[sid] = {'status': 'waiting', 'auth_code': None}
    qr_code_url = f"otpauth://totp/Example:{sid}?secret=yoursecret&issuer=Example"
    return jsonify({'qr_code_url': qr_code_url, 'sid': sid})

@app.route('/check_qrcode/<sid>', methods=['GET'])
def check_qrcode(sid):
    if sid in sessions:
        return jsonify({'status': sessions[sid]['status']})
    return jsonify({'status': 'invalid'}), 400

@app.route('/get_tokens', methods=['POST'])
def get_tokens():
    data = request.get_json()
    auth_code = data.get('auth_code')
    sid = data.get('sid')

    if not auth_code or not sid:
        return jsonify({'status': 'bad request'}), 400

    if sid in sessions and sessions[sid]['status'] == 'waiting':
        sessions[sid]['status'] = 'verified'
        sessions[sid]['auth_code'] = auth_code
        # Process auth_code here (e.g., verify against TOTP)
        return jsonify({'status': 'ok'})
    return jsonify({'status': 'invalid'}), 400

if __name__ == '__main__':
    app.run(debug=True)
