        self.app = Flask(__name__)
        self.chuliing = {'name': '', 'line': 0, 'end': True}
        self._setup_logging()
        self._setup_routes()

    def _setup_logging(self):
        handler = RotatingFileHandler('flask_tts.log', maxBytes=1024 * 1024 * 10, backupCount=5)
        handler.setLevel(logging.INFO)
        formatter = logging.Formatter('%(asctime)s - %(levelname)s - %(message)s')
        handler.setFormatter(formatter)
        self.app.logger.addHandler(handler)

    def _setup_routes(self):
        self.app.route('/apitts', methods=['POST'])(self.apitts)
        self.app.route('/ttslistjindu', methods=['GET'])(self.ttslistjindu)
        self.app.route('/ttslist', methods=['POST'])(self.ttslist)

    def apitts(self):
        try:
            text = request.form.get('text')
            language = request.form.get('language')
            voice = request.form.get('voice')
            if not text or not language:
                return jsonify({'code': 1, 'msg': 'Missing text or language'})
            tts = gTTS(text=text, lang=language, slow=False)
            filename = f"{hash(text)}.mp3"
            tts.save(filename)
            return jsonify({'code': 0, 'url': filename})
        except Exception as e:
            self.app.logger.error(f"Error in apitts: {e}")
            return jsonify({'code': 2, 'msg': str(e)})

    def ttslistjindu(self):
        return jsonify(self.chuliing)

    def ttslist(self):
        voice = request.form.get('voice')
        src = request.form.get('src')
        dst = request.form.get('dst')
        speed = float(request.form.get('speed', 1.0))
        language = request.form.get('language')
        if not all([voice, src, dst, language]):
            return jsonify({'code': 1, 'msg': 'Missing parameters'})
        threading.Thread(target=self.detail_task, args=(voice, src, dst, speed, language)).start()
        return jsonify({'code': 0, 'msg': 'ok'})

    def detail_task(self, voice, src, dst, speed, language):
        self.chuliing['end'] = False
        for filename in os.listdir(src):
            if filename.endswith('.txt'):
                self.chuliing['name'] = filename
                self.chuliing['line'] = 0
                with open(os.path.join(src, filename), 'r', encoding='utf-8') as f:
                    for line in f:
                        self.chuliing['line'] += 1
                        try:
                            tts = gTTS(text=line, lang=language, slow=False)
                            tts.save(os.path.join(dst, f"{filename[:-4]}_{self.chuliing['line']}.mp3"))
                        except Exception as e:
                            self.app.logger.error(f"Error processing line {self.chuliing['line']} in {filename}: {e}")
        self.chuliing['end'] = True

    def run(self):
        self.app.run(host='0.0.0.0', port=5000)

if __name__ == '__main__':
    api = FlaskTTSAPI()
    api.run()

