    if name in colors:
        return jsonify(colors[name])
    return jsonify({'error': 'Color not found'}), 404

@app.route('/colors', methods=['POST'])
def create_color():
    data = request.get_json()
    if not data or 'name' not in data or 'value' not in data:
        return jsonify({'error': 'Invalid data'}), 400
    colors = load_colors()
    if data['name'] in colors:
        return jsonify({'error': 'Color already exists'}), 409
    colors[data['name']] = data['value']
    save_colors(colors)
    return jsonify(data), 201

@app.route('/colors/<name>', methods=['PUT'])
def update_color(name):
    data = request.get_json()
    colors = load_colors()
    if name not in colors:
        return jsonify({'error': 'Color not found'}), 404
    if data and 'value' in data:
        colors[name] = data['value']
    save_colors(colors)
    return jsonify(colors[name])

@app.route('/colors/<name>', methods=['DELETE'])
def delete_color(name):
    colors = load_colors()
    if name not in colors:
        return jsonify({'error': 'Color not found'}), 404
    deleted_color = colors.pop(name)
    save_colors(colors)
    return jsonify(deleted_color)

