import flask
import dnstwist
import threading
import time
import json
import csv
import io
from urllib.parse import urlparse

class DNSTwistAPI:
    def __init__(self):
        self.app = flask.Flask(__name__)
        self.sessions = {}
        self._setup_routes()
        self._start_janitor()

    def _setup_routes(self):
        self.app.route('/')(self.root)
        self.app.route('/api/scan', methods=['POST'])(self.api_scan)
        self.app.route('/api/status/<sid>')(self.api_status)
        self.app.route('/api/domains/<sid>')(self.api_domains)
        self.app.route('/api/csv/<sid>')(self.api_csv)
        self.app.route('/api/json/<sid>')(self.api_json)
        self.app.route('/api/list/<sid>')(self.api_list)
        self.app.route('/api/stop/<sid>')(self.api_stop)

    def _start_janitor(self):
        thread = threading.Thread(target=self.janitor, args=(self.sessions,), daemon=True)
        thread.start()

    def janitor(self, sessions):
        while True:
            time.sleep(60)
            for sid, session in list(sessions.items()):
                if session.get('finished') or session.get('timestamp', 0) < time.time() - 3600:
                    del sessions[sid]

    def root(self):
        return flask.render_template('index.html')

    def api_scan(self):
        domain = flask.request.form.get('domain')
        if not domain:
            return flask.jsonify({'error': 'Domain is required'}), 400

        try:
            urlparse(domain)
        except:
            return flask.jsonify({'error': 'Invalid domain format'}), 400


        sid = dnstwist.generate_id()
        self.sessions[sid] = {'domain': domain, 'timestamp': time.time(), 'finished': False, 'results': []}
        thread = threading.Thread(target=self.scan_domain, args=(sid,), daemon=True)
        thread.start()
        return flask.jsonify({'sid': sid}), 200

    def scan_domain(self, sid):
        try:
            results = dnstwist.run(self.sessions[sid]['domain'], registered=True)
            self.sessions[sid]['results'] = results
        except Exception as e:
            self.sessions[sid]['error'] = str(e)
        finally:
            self.sessions[sid]['finished'] = True

    def api_status(self, sid):
        session = self.sessions.get(sid)
        if not session:
            return flask.jsonify({'error': 'Session not found'}), 404
        return flask.jsonify({'status': 'finished' if session.get('finished') else 'running'}), 200

    def api_domains(self, sid):
        session = self.sessions.get(sid)
        if not session:
            return flask.jsonify({'error': 'Session not found'}), 404
        return flask.jsonify(session.get('results', [])), 200

    def api_csv(self, sid):
        session = self.sessions.get(sid)
        if not session:
            return 'Session not found', 404, {}
        output = io.StringIO()
        writer = csv.writer(output)
        writer.writerow(dnstwist.DomainFuzzer.get_csv_header())
        for result in session.get('results', []):
            writer.writerow(result.get_csv_line())
        return output.getvalue(), 200, {'Content-Type': 'text/csv'}

    def api_json(self, sid):
        session = self.sessions.get(sid)
        if not session:
            return 'Session not found', 404, {}
        return json.dumps(session.get('results', [])), 200, {'Content-Type': 'application/json'}

    def api_list(self, sid):
        session = self.sessions.get(sid)
        if not session:
            return 'Session not found', 404, {}
        output = ""
        for result in session.get('results', []):
            output += result.domain + "\n"
        return output, 200, {'Content-Type': 'text/plain'}

    def api_stop(self, sid):
        if sid in self.sessions:
            self.sessions[sid]['finished'] = True
        return flask.jsonify({'status': 'stopped'}), 200

    def run(self, host, port):
        self.app.run(host=host, port=port)

