async def async_process_streaming_output(input_done, output_topic_name, result_handler, batch_size, kafka_bootstrap_servers, kafka_output_consumer_timeout_ms):
    consumer = None
    while not input_done.is_set():
        try:
            consumer = aiokafka.AIOKafkaConsumer(
                output_topic_name,
                bootstrap_servers=kafka_bootstrap_servers,
                enable_auto_commit=True,  # Or manage offset commits manually if needed
                auto_offset_reset="earliest",
                group_id="streaming_output_consumer_group" # Replace with a suitable group ID
            )
            await consumer.start()
            break  # Exit the loop if the consumer starts successfully
        except Exception as e:
            print(f"Error creating/starting Kafka consumer: {e}")
            if input_done.is_set():
                break
            await asyncio.sleep(5)  # Retry after 5 seconds

    if consumer is None:
        return  # Exit if consumer creation failed and input_done is set

    try:
        while not input_done.is_set():
            try:
                batch = await consumer.getmany(timeout_ms=kafka_output_consumer_timeout_ms, max_records=batch_size)
                if batch.get(output_topic_name):
                    result_handler(batch[output_topic_name])
            except asyncio.TimeoutError:
                pass  # Handle timeout as needed (e.g., check input_done)
            except Exception as e:
                print(f"Error consuming/processing messages: {e}")
                break  # Exit the inner loop on error
    finally:
        if consumer:
            await consumer.stop()

