import http.client
import urllib.parse
import logging
import sys

if sys.version_info[0] == 2:
    from urlparse import urlparse
else:
    from urllib.parse import urlparse

def http_request(method, action, param=None, **params):
    url_parts = urlparse(action)
    scheme = url_parts.scheme
    netloc = url_parts.netloc
    path = url_parts.path

    proxy_host = params.get('proxy_host')
    proxy_port = params.get('proxy_port')

    if proxy_host and proxy_port:
        if scheme == 'https':
            conn = http.client.HTTPSConnection(proxy_host, proxy_port)
            conn.set_tunnel(netloc, 443)
        else:
            conn = http.client.HTTPConnection(proxy_host, proxy_port)
            conn.set_tunnel(netloc, 80)
    elif scheme == 'https':
        conn = http.client.HTTPSConnection(netloc)
    else:
        conn = http.client.HTTPConnection(netloc)

    headers = params.get('headers', {})

    if param:
        if method == 'GET':
            path += '?' + urllib.parse.urlencode(param)
        elif method == 'POST':
            if 'Content-Type' not in headers:
                headers['Content-Type'] = 'application/x-www-form-urlencoded'
            body = urllib.parse.urlencode(param)
    else:
        body = None if method == 'GET' else params.get('body')

    try:
        conn.request(method, path, body=body, headers=headers)
        resp = conn.getresponse()
        if 200 <= resp.status < 300:
            return resp.read().decode('utf-8') if sys.version_info[0] >= 3 else resp.read()
        else:
            raise Exception("HTTP request failed with status: %s, reason: %s" % (resp.status, resp.reason))
    except Exception as e:
        logging.exception("Error during HTTP request: %s", e)
        raise
    finally:
        conn.close()

